mapper.insert:
INSERT INTO member (
email,
password,
nickname
, provider
) VALUES (
#{email},
#{password},
#{nickname}
, #{provider}
)

mapper.select:

SELECT  CASEWHEN(COUNT(*) = 1, 1, 0)
FROM    member
WHERE   email = #{email}

SELECT  CASEWHEN(COUNT(*) = 1, 1, 0)
FROM    member
WHERE   nickname = #{nickname}
